import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const [roomId, setRoomId] = useState('');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const [createLoading, setCreateLoading] = useState(false);
  const [joinLoading, setJoinLoading] = useState(false);
  const navigate = useNavigate();

  const handleCreateRoom = async () => {
    const promptUsername = prompt('Enter your username for the new room:');
    if (!promptUsername) {
      setError('Username required to create room');
      return;
    }
    setCreateLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/api/rooms/create', {
        name: 'New Project',
        language: 'javascript'
      });
      const colors = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7', '#DDA0DD'];
      const avatarColor = colors[Math.floor(Math.random() * colors.length)];
      navigate(`/editor/${response.data.roomId}`, { state: { username: promptUsername.trim(), avatarColor } });
    } catch (err) {
      const errorMsg = err.response?.data?.message || err.message || 'Failed to create room. Please ensure the server is running on port 5000.';
      setError(errorMsg);
    } finally {
      setCreateLoading(false);
    }
  };

  const handleJoinRoom = async () => {
    if (!roomId.trim() || !username.trim()) {
      setError('Please enter room ID and username');
      return;
    }
    setJoinLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/api/rooms/join', { roomId: roomId.trim(), username: username.trim() });
      navigate(`/editor/${roomId.trim()}`, { state: { username: username.trim(), avatarColor: response.data.avatarColor } });
    } catch (err) {
      const errorMsg = err.response?.data?.message || err.message || 'Failed to join room. Please check the room ID and ensure the server is running.';
      setError(errorMsg);
    } finally {
      setJoinLoading(false);
    }
  };

  // Adjust these for the form size
  const BOX_WIDTH = 400;
  const BOX_HEIGHT = 420;
  const RING_SIZE = Math.max(BOX_WIDTH, BOX_HEIGHT) + 40;

  return (
    <div className="relative flex items-center justify-center min-h-screen bg-gray-900">
      {/* Infinite scroll color ring, sized to match the form */}
      <div className="absolute flex items-center justify-center w-full h-full pointer-events-none">
        <div
          className="rounded-full border-8 border-transparent animate-spin-slow"
          style={{
            width: `${RING_SIZE}px`,
            height: `${RING_SIZE}px`,
            borderImage: 'conic-gradient(from 0deg, #ff6b6b, #ffeaa7, #45b7d1, #4ecdc4, #dda0dd, #ff6b6b 360deg) 1',
            borderStyle: 'solid',
          }}
        />
      </div>
      {/* Form container */}
      <div
        className="relative z-10 bg-gray-800 rounded-lg shadow-md p-8 flex flex-col items-center"
        style={{ width: BOX_WIDTH, minHeight: BOX_HEIGHT }}
      >
        <h1 className="text-3xl font-bold text-center text-white mb-8 flex items-center justify-center">
          <span className="mr-2 text-2xl">
            <img
              src="https://png.pngtree.com/element_our/png_detail/20181013/code-icon-design-vector-png_125856.jpg"
              alt="Code Icon"
              className="w-8 rounded-full"
            />
          </span>
          Code Collab
        </h1>

        {error && <div className="text-red-400 mb-4 text-center">{error}</div>}

        <div className="space-y-6 w-full">
          <button
            onClick={handleCreateRoom}
            disabled={createLoading}
            className="w-full bg-gradient-to-r from-purple-500 to-pink-500 text-white py-3 px-4 rounded-md hover:from-purple-600 hover:to-pink-600 disabled:opacity-50"
          >
            {createLoading ? 'Creating...' : 'Create New Room'}
          </button>

          <div className="text-center text-gray-400">or</div>

          <div>
            <input
              type="text"
              placeholder="Enter Room ID"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
              className="w-full px-3 py-2 border border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400 mb-4 bg-gray-700 text-white placeholder-gray-400"
            />
            <input
              type="text"
              placeholder="Enter Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full px-3 py-2 border border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400 mb-4 bg-gray-700 text-white placeholder-gray-400"
            />
            <button
              onClick={handleJoinRoom}
              disabled={joinLoading}
              className="w-full bg-gradient-to-r from-blue-500 to-cyan-500 text-white py-3 px-4 rounded-md hover:from-blue-600 hover:to-cyan-600 disabled:opacity-50"
            >
              {joinLoading ? 'Joining...' : 'Join Room'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
