import React, { useEffect, useRef, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import io from 'socket.io-client';
import Participants from './Participants';
import Chat from './Chat';

const CodeEditor = () => {
  const { roomId } = useParams();
  const location = useLocation();
  const [code, setCode] = useState('// Start coding here');
  const [language, setLanguage] = useState('javascript');
  const [theme, setTheme] = useState('light');
  const [participants, setParticipants] = useState([]);
  const [cursors, setCursors] = useState({});
  const [typingUsers, setTypingUsers] = useState([]);
  const [username, setUsername] = useState('');
  const [avatarColor, setAvatarColor] = useState('');
  const socketRef = useRef();
  const editorRef = useRef();
  const saveTimeoutRef = useRef();

  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
  }, [theme]);

  useEffect(() => {
    const state = location.state;
    if (state && state.username && state.avatarColor) {
      setUsername(state.username);
      setAvatarColor(state.avatarColor);
      connectSocket(state.username, state.avatarColor);
    } else {
      const promptUsername = prompt('Enter your username:');
      if (promptUsername) {
        setUsername(promptUsername);
        const colors = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7', '#DDA0DD'];
        const color = colors[Math.floor(Math.random() * colors.length)];
        setAvatarColor(color);
        connectSocket(promptUsername, color);
      }
    }
  }, [roomId, location]);

  const connectSocket = (user, color) => {
    const socket = io('http://localhost:5000');
    socketRef.current = socket;

    socket.emit('join-room', {
      roomId,
      username: user,
      avatarColor: color
    });

    socket.on('room-joined', (data) => {
      setCode(data.code);
      setLanguage(data.language);
      setParticipants(data.participants);
    });

    socket.on('code-updated', (data) => {
      setCode(data.code);
    });

    socket.on('cursor-moved', (data) => {
      setCursors(prev => ({ ...prev, [data.socketId]: data }));
    });

    socket.on('user-joined', (data) => {
      setParticipants(prev => [...prev, data]);
    });

    socket.on('user-left', (data) => {
      setParticipants(prev => prev.filter(p => p.socketId !== data.socketId));
      setCursors(prev => {
        const newCursors = { ...prev };
        delete newCursors[data.socketId];
        return newCursors;
      });
    });

    socket.on('user-typing', (data) => {
      setTypingUsers(prev => [...prev, data.socketId]);
    });

    socket.on('user-stopped-typing', (data) => {
      setTypingUsers(prev => prev.filter(id => id !== data.socketId));
    });

    socket.on('chat-message', (data) => {
      // Handle in Chat component
    });

    return () => {
      socket.emit('leave-room');
      socket.disconnect();
    };
  };

  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
    // Add cursor decorations for other users
    monaco.editor.defineTheme('custom-light', {
      base: 'vs',
      inherit: true,
      rules: [],
      colors: {
        'editor.background': '#f3f4f6',
      }
    });
    monaco.editor.defineTheme('custom-dark', {
      base: 'vs-dark',
      inherit: true,
      rules: [],
      colors: {
        'editor.background': '#1e1e1e',
        'editor.foreground': '#d4d4d4',
      }
    });
    monaco.editor.setTheme(theme === 'dark' ? 'custom-dark' : 'custom-light');
  };

  const toggleTheme = () => {
    setTheme(prev => prev === 'light' ? 'dark' : 'light');
  };

  const handleRunCode = async () => {
    if (language === 'javascript') {
      const iframe = document.createElement('iframe');
      iframe.style.display = 'none';
      iframe.sandbox = 'allow-scripts';
      document.body.appendChild(iframe);
      const doc = iframe.contentDocument || iframe.contentWindow.document;
      doc.open();
      doc.write(`
        <html>
          <head><title>JS Output</title></head>
          <body>
            <script>
              try {
                ${code}
              } catch (e) {
                document.body.innerHTML = '<pre style="color: red;">Error: ' + e.message + '</pre>';
              }
            </script>
          </body>
        </html>
      `);
      doc.close();
      setTimeout(() => {
        const output = iframe.contentWindow.document.body.innerHTML;
        alert('JS Output:\n' + output);
        document.body.removeChild(iframe);
      }, 100);
    } else if (language === 'html') {
      const blob = new Blob([code], { type: 'text/html' });
      const url = URL.createObjectURL(blob);
      window.open(url, '_blank');
    } else if (language === 'css') {
      const htmlWithCss = `
        <html>
          <head>
            <style>
              ${code}
            </style>
          </head>
          <body style="margin: 20px; font-family: Arial, sans-serif;">
            <h1>CSS Preview</h1>
            <p>This is a sample body to preview your CSS styles.</p>
            <div class="sample">Sample div for styling.</div>
          </body>
        </html>
      `;
      const blob = new Blob([htmlWithCss], { type: 'text/html' });
      const url = URL.createObjectURL(blob);
      window.open(url, '_blank');
    } else if (language === 'python') {
      if (!window.pyodide) {
        alert('Loading Python interpreter... Please wait and click Run again.');
        const script = document.createElement('script');
        script.src = 'https://cdn.jsdelivr.net/pyodide/v0.23.4/full/pyodide.js';
        document.head.appendChild(script);
        script.onload = async () => {
          window.pyodide = await loadPyodide({
            indexURL: 'https://cdn.jsdelivr.net/pyodide/v0.23.4/full/'
          });
          // Redirect stdout for output capture
          await window.pyodide.runPythonAsync(`
            import sys
            from io import StringIO
            old_stdout = sys.stdout
            sys.stdout = mystdout = StringIO()
          `);
          try {
            await window.pyodide.runPythonAsync(code);
          } catch (e) {
            alert('Python Error: ' + e.message);
            return;
          }
          const output = mystdout.getvalue();
          sys.stdout = old_stdout;
          alert('Python Output:\n' + (output || 'No output (check for prints).'));
        };
        return;
      }
      // Redirect stdout for output capture
      await window.pyodide.runPythonAsync(`
        import sys
        from io import StringIO
        old_stdout = sys.stdout
        sys.stdout = mystdout = StringIO()
      `);
      try {
        await window.pyodide.runPythonAsync(code);
      } catch (e) {
        alert('Python Error: ' + e.message);
        return;
      }
      const output = await window.pyodide.runPythonAsync('mystdout.getvalue()');
      await window.pyodide.runPythonAsync('sys.stdout = old_stdout');
      alert('Python Output:\n' + (output || 'No output (check for prints).'));
    }
  };

  const handleCodeChange = (value) => {
    setCode(value);
    if (socketRef.current) {
      socketRef.current.emit('code-change', {
        roomId,
        code: value,
        timestamp: Date.now()
      });
      // Auto-save every 30 seconds
      if (saveTimeoutRef.current) clearTimeout(saveTimeoutRef.current);
      saveTimeoutRef.current = setTimeout(() => {
        socketRef.current.emit('save-snapshot', { roomId, code: value });
      }, 30000);
    }
  };

  const handleCursorChange = (e) => {
    if (socketRef.current && e) {
      socketRef.current.emit('cursor-update', {
        roomId,
        position: e.position,
        selection: e.selection
      });
    }
  };

  const handleTypingStart = () => {
    if (socketRef.current) {
      socketRef.current.emit('typing-start', { roomId });
    }
  };

  const handleTypingStop = () => {
    if (socketRef.current) {
      socketRef.current.emit('typing-stop', { roomId });
    }
  };

  return (
    <div className={`h-screen flex flex-col ${theme === 'dark' ? 'bg-gray-900' : 'bg-gray-100'}`}>
      <header className={`shadow px-4 py-2 flex justify-between items-center ${theme === 'dark' ? 'bg-gray-800 text-white' : 'bg-white'}`}>
        <h1 className="text-xl font-bold">Room: {roomId}</h1>
        <div className="flex items-center space-x-4">
          <button
            onClick={toggleTheme}
            className="px-3 py-1 bg-gray-200 dark:bg-gray-700 rounded text-sm"
          >
            {theme === 'dark' ? 'Light' : 'Dark'} Mode
          </button>
          <button
            onClick={handleRunCode}
            className="px-3 py-1 bg-green-500 text-white rounded text-sm hover:bg-green-600"
          >
            Run
          </button>
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            className="border rounded px-2 py-1 dark:bg-gray-700 dark:border-gray-600"
          >
            <option value="javascript">JavaScript</option>
            <option value="python">Python</option>
            <option value="html">HTML</option>
            <option value="css">CSS</option>
          </select>
          <Participants participants={participants} />
        </div>
      </header>

      <div className="flex flex-1 overflow-hidden">
        <div className="flex-1 relative">
          <Editor
            height="100%"
            language={language}
            value={code}
            onChange={handleCodeChange}
            onMount={handleEditorDidMount}
            theme={theme === 'dark' ? 'vs-dark' : 'vs'}
            options={{
              minimap: { enabled: false },
              fontSize: 14,
              lineNumbers: 'on',
              roundedSelection: false,
              scrollBeyondLastLine: false,
              automaticLayout: true,
            }}
            onMouseDown={handleTypingStart}
            onKeyDown={handleTypingStart}
            onKeyUp={handleTypingStop}
            onMouseUp={handleTypingStop}
          />
          {typingUsers.length > 0 && (
            <div className={`absolute top-2 right-2 px-2 py-1 rounded text-sm ${theme === 'dark' ? 'bg-yellow-800 text-yellow-200' : 'bg-yellow-200'}`}>
              {typingUsers.length} user(s) typing...
            </div>
          )}
        </div>
        <Chat socket={socketRef.current} roomId={roomId} username={username} theme={theme} />
      </div>
    </div>
  );
};

export default CodeEditor;
